/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} csv - CSV string with column headers
	@return {String} return JSON object using column headers and field keys
*/
function convertCsvToJson(csv) {
	var textXML = SerializerFactory.getSerializer('DELIMITED').toXML(csv);
	var xml = new XML(textXML);
	var dataItems = [];
	for (var i = 1; i <= xml['row'].length() - 1; i++) {
		var jsonData = {};
		for (var j = 1; j <= xml['row'][0].children().length(); j++) {
			var column = 'column' + j.toString();
			var header = xml['row'][0][column].toString().replace(/\s/g, '_').replace(/-/g, '_').toLowerCase();
			var value = xml['row'][i][column].toString();

			jsonData[header] = value;

		}
		dataItems.push(jsonData);
	}

	return JSON.stringify(dataItems);
}

/**
	Converts JSON to CSV.
**/
/*	@param {String} jsonData - JSON string with [0] index key as headers for csv
	@return {String} return csv
*/	

function convertJsonToCsv(data) {
	try{
		var json = JSON.parse(data);	
		var fields = Object.keys(json[0])
		var replacer = function(key, value) { return value === null ? '' : value } 
		var csv = json.map(function(row){
		  return fields.map(function(fieldName){
		    return JSON.stringify(row[fieldName], replacer)
		  }).join(',')
		})
		csv.unshift(fields.join(',')) // add header column
		csv = csv.join('\r\n');
		return csv;
	}catch(error){
		return error;
	}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function sendMessageToChannelDestination(channelID, destinationID, map, data) {
	var msgMap;
	var outData = {};

	if (!map) {
		msgMap = Maps.map();
	} else {
		msgMap = Maps.map(map);
	}

	if (data == null || data == undefined) {
		data = '';
	}

	if (msgMap.get('sourceChannelId') == null) {
		msgMap.add('sourceChannelId', channelId);
	}
	if (msgMap.get('sourceChannelName') == null) {
		msgMap.add('sourceChannelName', channelName);
	}

	// Dirty way to handle connector message not being defined. Handles cases where this function 
	// is used in a source js writer.
	try {
		if (msgMap.get('sourceMessageId') == null && connectorMessage && connectorMessage.getMessageId()) {
			msgMap.add('sourceMessageId', connectorMessage.getMessageId().toString());
		}
	} catch(ex) {}

	var msgRequest = new RawMessage(data, [destinationID], msgMap);
	var msgResponse = router.routeMessageByChannelId(channelID, msgRequest);

	if (msgResponse == null) {
		// return;
		throw ChannelUtil.getChannelName(channelID) + '" does not return a valid response object'; 
	}
	if (msgResponse.getStatus() == null) {
		throw ChannelUtil.getChannelName(channelID) + '" does not return a valid response object';
	}

	if (msgResponse.getStatus() == 'ERROR') {
		throw msgResponse.getMessage();
	}

	return msgResponse.getMessage();


	// var msgResponse;

	/*
	try {
		msgResponse = router.routeMessageByChannelId(channelID, msgRequest);
		outData.status = 'success';
		outData.status_message = 'message successfully sent to channel';
	} catch (ex) {
		outData.status = 'error';
		outData.status_message = ex.toString();
		return outData;
	}

	if (msgResponse.getStatus() == null) {
		outData.response_status = 'unknown';
		logger.error('Code Template "Send message to channel by id": channel "' + ChannelUtil.getChannelName(channelID) + '" does not return a valid response object');
	}

	switch (String(msgResponse.getStatus())) {
		case 'ERROR':
			outData.response_status = 'error';
			break;
		case 'FILTERED':
			outData.response_status = 'filtered';
			break;
		case 'QUEUED':
			outData.response_status = 'queued';
			break;
		case 'RECEIVED':
			outData.response_status = 'received';
			break;
		case 'SENT':
			outData.response_status = 'sent';
			break;
	}

	outData.response_message = msgResponse.getMessage();

	return outData;
	*/
}

/**
	Get a new bots database connection. Be sure to wrap in try/finally block to close database connection.

	@param {String} environment - prod, staging, dev, local
	@return {String} return database connection
*/
function GetBotsDBConnection() {
	var username = configurationMap.get('bots-db-user');
	var password = configurationMap.get('bots-db-pass');
	var uri = configurationMap.get('bots-db-uri');	
	return DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver', uri, username, password);
}

/**
	Get a new database connection. 

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function GetDBConnection(environment) {
	if (environment != 'prod' && environment != 'staging' && environment != 'dev' && environment != 'local') {
		return null;
	}

	var username = configurationMap.get('docsink-db-user-' + environment);
	var password = configurationMap.get('docsink-db-pass-' + environment);
	var uri = configurationMap.get('docsink-db-uri-' + environment);

	return DatabaseConnectionFactory.createDatabaseConnection('com.mysql.cj.jdbc.Driver', uri, username, password);
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function generateChannelResponse() {
	// This script executes once after a message has been processed
	// Responses returned from here will be stored as "Postprocessor" in the response map

	var destinations = sourceMap.get('destinationSet');
	var metadataIds = destinations.toArray();

	if (metadataIds.length == 0) {
		return ResponseFactory.getErrorResponse('invalid request');
	}

	var smsg = message.getConnectorMessages().get(0);
	if (smsg.getStatus() == 'ERROR') {
		if (smsg.getProcessingError()) {
			return ResponseFactory.getErrorResponse(smsg.getProcessingError());
		}
		return ResponseFactory.getErrorResponse(smsg.getResponseData().getMessage());
	}

	var metadataID = metadataIds[0];
	var cmsg = message.getConnectorMessages().get(metadataID);

	if (cmsg.getStatus() == 'ERROR') {
		if (cmsg.getProcessingError()) {
			return ResponseFactory.getErrorResponse(cmsg.getProcessingError());
		}
		return ResponseFactory.getErrorResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'SENT') {
		return ResponseFactory.getSentResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'FILTERED') {
		return ResponseFactory.getFilteredResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'QUEUED') {
		return ResponseFactory.getQueuedResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'RECEIVED') {
		return ResponseFactory.getSentResponse(cmsg.getResponseData().getMessage());
	} else {
		return cmsg;
	}
}

/**
	Desc: Perform sFTP Put Javascript to connect over SFTP and Put a file on the remote disk
	Name:  Perform sFTP Put Action (ip, port, user, pass, filename, msg)
	@param {String} ip - provide the IP address
		  {String} port - provide the port number
		  {String} user - provide the username
		  {String} pass - provide the password
		  {String} filename - provide the filename
		  {String} msg - XML presentation of the message to be sent as a file
	@return {String} return response (Success or Failure)
*/
function sftpPut(ip, port, user, pass, filename, msg){
	importPackage(com.jcraft.jsch);
	importPackage(java.io);
	importPackage(java.lang);
	importPackage(java.nio.charset);

	function toByteArray(str){
		var bytes = [];
		for (var i = 0; i < str.length; ++i) {
    		bytes.push(str.charCodeAt(i));
		}
		return bytes;
	}

	// testing vars
	var debug= 0;

	//create object.
	var jsch = new JSch();
	jsch.setConfig('StrictHostKeyChecking','no');
	var session = jsch.getSession(user,ip,port);
	session.setPassword(pass);
	session.setTimeout(10000);
	session.connect();
	var channel = session.openChannel('sftp');
	var ioexception = new Packages.java.io.IOException;
	channel.connect();
	if(debug>0) {logger.info('Connected to SFTP: ');}
	if(debug>0) {logger.info('Home Directory: ' + channel.getHome());}
	channel.cd('/'); //Folder on the SFTP server where file will be found

	var filename = filename;
	var filebody = msg;
	channel.put(filebody);

	channel.disconnect();
	session.disconnect();	
	return;
}

/**
	Desc: This script is used to evaluate a string if contains a number

	scriptName: contains_number
	@param {String} arg1 - arg1 description
	@return {boolean} return true or false depending if isNaN() - if isNan() -> return true otherwise -> return false
	e.g., write_msg_to_disk($('rawhl7'),api_error_directory,$('ark_uuid'));
	History:
*/
function contains_number(text) {

	var scriptName= 'contains_number';
	var debug= 0;

	if(debug>0) { logger.error('DEBUG::'+scriptName+' verify text: '+text); }

	if((typeof(text) !== 'undefined') && (text.toString().length>0))  {

		if((text.toString()== 'DETECTED')||(text.toString()== 'TRUE')) {
			return false;
		} else if(text.toString()== 'NOT DETECTED') {
			return false;
		} else if(text.toString()== 'INVALID') {
			return false;
		} else {
			var arrText= text.toString().split('');
			if(debug>0) { logger.error('DEBUG::'+scriptName+' arrText: '+JSON.stringify(arrText)); }
			// curObservationValue.toString() !== 'NaN')
			for each(strElement in arrText) {
				// if(debug>0) { logger.error('DEBUG::'+scriptName+' element: '+element+' isNaN(): '+element.toString().isNaN()); }
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' typeof element: '+typeof(strElement));
					logger.error('DEBUG::'+scriptName+' toString: '+strElement.toString());
					logger.error('DEBUG::'+scriptName+' element toString() !== isNaN(): '+(strElement.toString()!== 'NaN')); 
				}
				if(strElement.toString() !== 'NaN') {
					return true;
				} // end if
			} // end for
		} // end if 
		return false;
	} // end if
}

/**
	Converts standard HL7 v2.x date format to human-readable ISO date format
*/

/*
	Author: Datica Inc.
	toISODate: Returns an ISO-formatted date string with specified timezone offset

	Arguments
	---------
		Required
		--------
			hl7DateString:		An HL7-formatted date string (ex: "yyyyMMddHHmmss"

		Optional
		--------
			timezome:			A timezone identifier string corresponding to one of the java.util.TimeZone values (ex: "US/Central")
*/

function toISODate( hl7DateString, timezone ) {
	var isoDate;
	var dateString;
	var hl7Formatter;
	var isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");

	if (timezone) {
		isoFormatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(timezone));
	}

	if ( hl7DateString != null && !isNaN(hl7DateString.substring(0,13)) && hl7DateString.length > 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
	}
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 12 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 8 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");
	} 
	else dateString = hl7DateString;

	try {
		if (hl7Formatter) {
			isoDate = hl7Formatter.parse(hl7DateString);
			isoDate = isoFormatter.format(isoDate);
			dateString = new String(isoDate);
		}
	} catch (err) {
		dateString = hl7DateString;
	} finally {
		return dateString;
	}
}

/**
	Convert HL7 Ethnic Code to Ethnicity Coded ELement (CWS) data type

	@scriptName: hl7_modify_ethnic_code_pid22_to_ethnic_coded_element
	@descName:   Convert HL7 Ethnic Code to Ethnicity Coded ELement Data Type
	@authorName: Jeffrey Ritz
	Parameters:
	@param {XML} pid22 - contains the XML HL7 representation of the PID-22 element
	@return {XML} newEthnicity - returns the modified XML HL7 representation of the PID-22 element
*/
function hl7_modify_ethnic_code_pid22_to_ethnic_coded_element(pid22) {

	var scriptName= 'hl7_modify_ethnic_code_pid22_to_ethnic_coded_element'
	var debug= 0;

	// 2023-03-07 JER - Added code per Streamline Issues List - 20220306 item# 3
	// https://phinvads.cdc.gov/vads/ViewValueSet.action?id=2088950C-6EF8-E111-B875-001A4BE7FA90
	var newEthnicity= new XML('<PID.22></PID.22>');

	if(typeof(pid22)!== 'undefined') {

		for each(element in pid22) {

			if(element['PID.22.1'].toString().length>0) {
				var pid221= element['PID.22.1'].toString();
				switch (pid221) {
					case 'H': 
						var strPid22= pid221+'^Hispanic or Latino^HL70189';
						break;
					case 'N': 
						var strPid22= pid221+'^Not Hispanic or Latino^HL70189';
						break;
					case 'U':
						var strPid22= pid221+'^Unknown^HL70189';
						break;
					default: 
						var strPid22= 'U^Unknown^HL70189';
				} // end switch
			} else {
				var strPid22= '';
			} // end if 
			newEthnicity.appendChild(strPid22);
		} // end for each

	} // end if 

	return newEthnicity;

} // end function

