			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.telecomEquipmentType= 	child.toString();
			} else {
				objExtendedTelecommunicationNumber.telecomEquipmentType=	'';
			} // end if 
		} // end if 

		if(i== 3) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.emailAddress= 			child.toString();
			} else {
				objExtendedTelecommunicationNumber.emailAddress=			'';
			} // end if 
		} // end if 

		if(i== 4) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.countryCode= 			child.toString();
			} else {
				objExtendedTelecommunicationNumber.countryCode=			'';
			} // end if 
		} // end if 

		if(i== 5) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.areaCityCode= 			child.toString();
			} else {
				objExtendedTelecommunicationNumber.areaCityCode=			'';
			} // end if 
		} // end if 

		if(i== 6) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.localNumber= 			child.toString();
			} else {
				objExtendedTelecommunicationNumber.localNumber=			'';
			} // end if 
		} // end if 

		if(i== 7) {	
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.areaCityCode= 			child.toString();
			} else {	
				objExtendedTelecommunicationNumber.areaCityCode=			'';
			} // end if 
		} // end if 

		if(i== 8) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.extension= 			child.toString();
			} else {
				objExtendedTelecommunicationNumber.extension=			'';
			} // end if 
		} // end if 

		if(i== 9) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.anyText= 				child.toString();
			} else {
				objExtendedTelecommunicationNumber.anyText=				'';
			} // end if 
		} // end if 

		if(i== 10) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.extensionPrefix= 		child.toString();
			} else {
				objExtendedTelecommunicationNumber.extensionPrefix=		'';
			} // end if
		} // end if  

		if(i== 11) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.speedDialCode= 		child.toString();
			} else {
				objExtendedTelecommunicationNumber.speedDialCode=			'';
			} // end if
		} // end if 

		if(i== 12) {
			if(child.toString().length>0) {
				objExtendedTelecommunicationNumber.unformatTelephoneNumber= 	child.toString();
			} else {
				objExtendedTelecommunicationNumber.unformatTelephoneNumber=		'';
			} // end if
		} // end if 

		i++;
	} // end for each 

	if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' objExtendedTelecommunicationNumber typeof: '+typeof(objExtendedTelecommunicationNumber)+' value: '+JSON.stringify(objExtendedTelecommunicationNumber));

	return objExtendedTelecommunicationNumber;

} // end function

/**
	To verify if one string (source) contains another string (target).

	@param {String} arg1 - target string to lookup in the source string
	@return {String} return true if the source string contains the target string
*/
String.prototype.contains = function(arg1) {

	return this.indexOf(arg1) > -1;

};

/**
	To verify if the given string contains HTTP status code.

	@param - no parameter
	@return {String} return true if the string contains the HTTP status code, false otherwise
*/

function isHttpCode2xx(arg1) {
	var code2xx = ['200','201','202','203','204','205','206','207','208'];	
	var length = code2xx.length;

	while(length--) {
   		if (arg1.indexOf(code2xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

function isHttpCode4xx(arg1) {
	var code4xx = ['400','401','402','403','404','405','406','407','408','409','410','411','412','413','414','415','416','417','429','431','451'];	
	var length = code4xx.length;

	while(length--) {
   		if (arg1.indexOf(code4xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

function isHttpCode5xx(arg1) {
	var code5xx = ['500','501','502','503','504','505','506','510','511'];	
	var length = code5xx.length;

	while(length--) {
   		if (arg1.indexOf(code5xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

