/**
	This creates a header for csv files based on the contents of the header variable.  The header
	variable is populated in the channel from the outbound msg template

	@return {String} return description
*/
function createHeader() {
	//Destination transformer.  this creates the header for each new file

	var path = java.nio.file.Paths.get($('filePath')+$('fileName'));
	var bytes = (new java.lang.String($('header'))).getBytes("UTF-8");
	try {
	  java.nio.file.Files.write(path, bytes, java.nio.file.StandardOpenOption.CREATE_NEW);
	}
	catch (e) {
	  //logger.info(" should throw IOException if file already exists");

	}
}

/**  This code works by calculating a checksum for each provided message.  We save off the most recent 100 unique checksum values in a global 
	array so if the current message has been recently received we are able to ignore it.

	@author:  	Jeffrey Ritz
	@scriptName: 	filterEvaluateReceivedChecksum.js
	@created: 	2023-03-09
	@param {object} msg - Inbound connectorMessage.getRawData()
	@param {boolean} filter - return filter boolean (true= filter msg, false= do not filter msg)
		persist received_checksum global ChannelMap array
		persist filterMessage ChannelMap variable
	@history
*/
function filterEvaluateReceivedChecksum(msg) {	

	var scriptName= 'filterEvaluateReceivedChecksum'
	var filter = false;
	var received_len_limit = 999;
	var debug= 0;
	if(typeof(msg)=== 'object') var msg= JSON.stringify(msg);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' A msg typeof: '+typeof(msg)+' msg: '+msg);
		logger.error('DEBUG::'+scriptName+' B connectorMessage.getRawData() typeof: '+typeof(connectorMessage.getRawData())+' msg: '+connectorMessage.getRawData());
	} // end if 

	//calculate checksum for current message (ignoring MSH and ORC segments)
	var checksum = new java.util.zip.CRC32();
	// var dataBytes = new java.lang.String(connectorMessage.getRawData()).getBytes('UTF-8');
	// message = new String(message.getBytes("utf-8"), "utf-8");
	if(debug>0) logger.error('DEBUG::'+scriptName+' C msg typeof: '+typeof(msg)+' msg: '+msg);
	// var dataBytes = new java.lang.String(msg.getBytes('UTF-8'));
	var dataBytes = new java.lang.String(msg).getBytes('UTF-8');
	if(debug>0) logger.error('DEBUG::'+scriptName+' D dataBytes msg typeof: '+typeof(dataBytes)+' dataBytes: '+dataBytes);

	// var messageObjectgetBytes= checksum.update(connectorMessage.getRawData().getBytes());
	if(debug>0) logger.error('DEBUG::'+scriptName+' E msg typeof: '+typeof(msg)+' msg: '+msg);
	var messageObjectgetBytes= checksum.update(dataBytes);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' msg typeof: '+typeof(msg)+' msg.getBytes(): '+dataBytes);
		logger.error('DEBUG::'+scriptName+' messageObjectgetBytes typeof: '+typeof(messageObjectgetBytes)+' messageObjectgetBytes: '+messageObjectgetBytes);
	} // end if 

	$c('checksum', checksum.getValue());

	//grab the global channel map variable holding the last X unique checksums that we've received
	var received = $gc('received_checksums');

	if (received == undefined) {
		var received = new Array();
		received.push(checksum.getValue());
	} else {
		//check if we've recently received this message, skip it if so
		if (received.indexOf(checksum.getValue()) > -1) {
			filter = true;
		//we haven't seen it recently, so remember it and allow it through
		} else {
			var received_len = received.length;
			if (received_len > received_len_limit) {
				//remove oldest/first entry, shift all entries forward, and add new entry to end
				received.shift();
				received[received_len_limit] = checksum.getValue();
			} else {
				//add new entry to end of array
				received[received_len] = checksum.getValue();
			} // end if 
		} // end if 
	} // end if 

	 //debug code
	/*for (x=0; x<=received_len_limit; x++) {
		$c('received-'+x, received[x]);
	} */

	$c('filterMessage',filter);
	$gc('received_checksums',received);

	return filter;

} // end function

/**  This code works by calculating and reutrning a checksum for each provided message

	@author:  	Jeffrey Ritz
	@scriptName: 	getMessageChecksum.js
	@created: 	2023-03-09
	@param {object} msg - Inbound connectorMessage.getRawData()
	@param {object} result - return checksum value
	@history
	2023-03-15 JER - Modified to push in values into the arrChecksumValue new Array() to remove the exponential representation of the number value
*/
function getMessageChecksum(msg) {	

	var scriptName= 'getMessageChecksum';
	var debug= 0;
	if(typeof(msg)=== 'object') var msg= JSON.stringify(msg);

	//calculate checksum for current message (ignoring MSH and ORC segments)
	var checksum = new java.util.zip.CRC32();
	var dataBytes = new java.lang.String(msg).getBytes('UTF-8');
	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' D dataBytes msg typeof: '+typeof(dataBytes)+' dataBytes: '+dataBytes);
		logger.error('DEBUG::'+scriptName+' E msg typeof: '+typeof(msg)+' msg: '+msg);
	} // end if 
	var messageObjectgetBytes= checksum.update(dataBytes);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' msg typeof: '+typeof(msg)+' msg.getBytes(): '+dataBytes);
		logger.error('DEBUG::'+scriptName+' messageObjectgetBytes typeof: '+typeof(messageObjectgetBytes)+' messageObjectgetBytes: '+messageObjectgetBytes);
	} // end if 

	var checksumValue= checksum.getValue();
	// 2023-03-15 JER - Modified to push in values into the arrChecksumValue new Array() 
	var arrChecksumValue= new Array(); 
	arrChecksumValue.push(checksumValue);
	$c('checksum', arrChecksumValue);

	return checksumValue;

} // end function

/**  This function will remove a JSON child object and it's descendents from a parent object JSON

	@author:  	Jeffrey Ritz
	@scriptName: 	removeChildObjectFromParentObjectJson.js
	@created: 	2023-03-09
	@param {object} msg - Inbound connectorMessage.getRawData()
		{string} parentObject - place the name of the string to be designated as the parent object in the JSON
		{string} childObject  - place the name of the string to be designated as the child object to be deleted from the JSON
	@param {object} msg - return modified Json object msg excluding specified Json child object
	@history
*/
function removeChildObjectFromParentObjectJson(msg, parentObject, childObject) {

	var scriptName= 'removeChildObjectFromParentObjectJson';
	var debug= 0;

	//var strObjJson= JSON.parse(msg);
	var strObjJson= JSON.stringify(msg);

	//var objJson= JSON.parse(msg);
	var newParentObjects= new Object();
	var parentObjects= msg;
	var arrParentObjects= new Array();
	var arrTransactionObjects= new Array();

	if(debug>0) logger.error('DEBUG::'+scriptName+' typeof parentObjects: '+typeof(parentObjects));
	for (var key in parentObjects) {
		if(debug>0) logger.error('DEBUG::'+scriptName+' Parent Objects typeof key: '+typeof(key)+' key: '+key);
		if(key== parentObject) {
			if(debug>0) logger.error('DEBUG::'+scriptName+' Parent Objects typeof objJson[key]: '+typeof(parentObjects[key])+' parentObjects[key]: '+JSON.stringify(parentObjects[key]));

			var newChildObjects= new Object();
			var childObjects= parentObjects[key];
			var arrChildObjects= new Array();

			for(var key2 in childObjects) {
				if(debug>0) logger.error('DEBUG::'+scriptName+' typeof key2: '+typeof(key2)+' key2: '+key2+' childObject: '+childObject);
				for(var key3 in childObjects[key2]) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' typeof key3: '+typeof(key3)+' key3: '+key3+' childObject: '+childObject);
					if(key3== childObject) {
						var transactionObject= new Object();
						if(debug>0) logger.error('DEBUG::'+scriptName+' childObject typeof: '+typeof(childObjects[key2][key3])+' childObject: '+JSON.stringify(childObjects[key2][key3]));
						transactionObject.key= key3;
						transactionObject.value= childObjects[key2][key3];
						delete childObjects[key2][key3];
						if(debug>0) logger.info('DEBUG::'+scriptName+' DELETE childObject: '+key3+' of parentObject: '+JSON.stringify(childObjects[key2]));
						arrTransactionObjects.push(transactionObject);
					} // end if
				} // end for 
				arrChildObjects.push(childObjects[key2]);
			} // end for
			newParentObjects[key]= arrChildObjects;
			if(debug>0) logger.error('DEBUG::'+scriptName+' Add all child keys: '+JSON.stringify(arrChildObjects)+' into ParentObject['+key+']: '+newParentObjects[key]);
		} else {
			newParentObjects[key]= parentObjects[key];
			if(debug>0) logger.error('DEBUG::'+scriptName+' Add key: '+key+' object: '+parentObjects[key]+' string: '+JSON.stringify(parentObjects[key]));
		} // end if
		arrParentObjects.push(newParentObjects);
	} // end for 
	var newMsg= arrParentObjects;
	channelMap.put('removeChildObjectMsg',newMsg);
	channelMap.put('removedChildTransIds',arrTransactionObjects);

	if(debug>0) logger.error('DEBUG::'+scriptName+' AFTER msg: '+JSON.stringify(newMsg));
	return newMsg;

}

/**  This function will remove a JSON object and it's descendents from the msg

	@author:  	Jeffrey Ritz
	@scriptName: 	removeObjectFromJson.js
	@created: 	2023-03-09
	@param {string} msg - Inbound connectorMessage.getRawData()
	@param {string} msg - return modified msg excluding specified Json object from msg
	@history
	2023-03-15 JER - Modified to include removeObject function parameter to specify specific JSON object by referencing key to be removed
*/
function removeObjectFromJson(msg, removeObject) {

	var scriptName= 'removeObjectFromJson';
	var debug= 0;
	//var objJson= JSON.stringify(msg);
	var objJson= JSON.parse(msg);
	var newObjJson= new Object();

	if(debug>0) logger.error('DEBUG::'+scriptName+' typeof objJson: '+typeof(objJson)+' objJSON: '+JSON.stringify(objJson));
	for (var key in objJson) {
		if(key== removeObject) {
			if(debug>0) logger.error('DEBUG::'+scriptName+' DELETE key: '+key);
			// delete objJson[key];
		} else {
			newObjJson[key]= objJson[key];
			if(debug>0) logger.error('DEBUG::'+scriptName+' Add key: '+key+' object: '+objJson[key]+' string: '+JSON.stringify(objJson[key]));
		} // end if 
	} // end for 
	var newMsg= newObjJson;

	if(debug>0) logger.error('DEBUG::'+scriptName+' AFTER msg: '+JSON.stringify(newMsg));

	// return JSON.stringify(newMsg);
	return newMsg;
}

/**
	The purpose of this function is to update the status in Insight.
	Populate the channel map with the following variables functionalGroupControlNumber, status, eventDate

