/** createHL7FromObject

  	- Desc: This function receives an Object and returns formed HL7 in XML format
  	- Modified
	2020-03-04 JER - Created

Message Structure

MSH|^~\&|||||||<Event>||<Processing ID>|<Version>|
PID|||<Patient Identifiers>||<Patient Name>||<Date of Birth>|<Sex>||||||||||<PatientVisit ID>|<SSN>|
PV1|||<Patient Location>||||||||||||||||<Patient Visit ID>|
TXA||<Report Type>||<Activity Date/Time>|<Primary Provider ID>|<Origination
Date/Time>|<Transcription Date/Time>|||<Assigned Document Authenticator ID>||<Document ID>|||||<Document Completion Status>||<Document Availability Status>|
OBX||<Value Type>|||<Observation Value>|||<Abnormal Flag>|


Data Element Details
Required
	PID-3: Patient Identifiers
	TXA-2: Report Type
	TXA-4: Activity Date/Time
	TXA-5: Primary Activity Provider ID
 	TXA-12: Document ID - Unique ID that globally identifies a document
	OBX-2: Value Type - TX or FT for transcriptions. RP for reference pointers
	OBX-5: Observation Value - Body of the document


   	@param {String} msg - this contains a RAW message (i.e. msg)
	@return {Object} objMsgContent - this object contains objectification of the RAW msg string
*/
function createHL7FromObject (msgObject, debug) {

	var debug= 0;  // 0= OFF, 1= ON
	var currDateTime= getCurrentDateTime();	// only supports YYYYMMDDHHMMSS
	var transDescription= 'createHL7FromObject';
	var objMsgContent= msgObject;

	// set or modify these values as needed
	var setID = '1';
	var msgCode= 'MDM';
	var eventType= 'T02';
	channelMap.put('msgType',msgCode+'^'+eventType);
	channelMap.put('currentUUID',UUIDGenerator.getUUID());
	channelMap.put('messageId',$('currentUUID'));

	var sendingApplication= 'ABC-APPLICATION';
	var receivingApplication= 'EPIC'
	var messageProcessingId= 'P';  // reference HL7 Table 0103 WHERE P= Production, T= Test, D= Debugging, N= Non-Production, V= Valdation
	var hl7version= '2.3';
	var patientClass= 'I';  		// reference HL7 Table 0004 https://www.hl7.org/fhir/v2/0004/index.html
	//
	tmp['MSH']['MSH.3']['MSH.3.1']= sendingApplication;
	tmp['MSH']['MSH.4']['MSH.4.1']= channelMap.get('SendingFacility');
	tmp['MSH']['MSH.5']['MSH.5.1']= receivingApplication;
	tmp['MSH']['MSH.6']['MSH.6.1']= channelMap.get('ReceivingFacility');
	tmp['MSH']['MSH.7']['MSH.7.1']= currDateTime;
	tmp['MSH']['MSH.8']['MSH.8.1']= '';
	tmp['MSH']['MSH.9']['MSH.9.1']= msgCode;
	tmp['MSH']['MSH.9']['MSH.9.2']= eventType;
	tmp['MSH']['MSH.10']['MSH.10.1']= $('currentUUID');
	tmp['MSH']['MSH.11']['MSH.11.1']= messageProcessingId;
	tmp['MSH']['MSH.12']['MSH.12.1']= hl7version;

	tmp['PID']['PID.1']['PID.1.1'] = setID;
	tmp['PID']['PID.3']['PID.3.1']= objMsgContent.PatId;
	tmp['PID']['PID.5']['PID.5.1']= objMsgContent.PatientLastNm;
	tmp['PID']['PID.5']['PID.5.2']= objMsgContent.PatientFirstNm;
	tmp['PID']['PID.5']['PID.5.3']= objMsgContent.PatientMiddleNm;
	tmp['PID']['PID.7']['PID.7.1']= objMsgContent.PatientDOB;
	tmp['PID']['PID.8']['PID.8.1']= objMsgContent.PatientGender.toString().substring(0, 1);
	tmp['PID']['PID.18']['PID.18.1']= objMsgContent.EncounterId;
	tmp['PID']['PID.19']['PID.19.1']= objMsgContent.PatientSSN;

	tmp['PV1']['PV1.1']['PV1.1.1'] = setID;
	tmp['PV1']['PV1.19']['PV1.19.1'] = objMsgContent.EncounterId;

	tmp['TXA']['TXA.1']['TXA.1.1'] = setID;
	tmp['TXA']['TXA.2']['TXA.2.1'] = objMsgContent.ReportType;
	tmp['TXA']['TXA.4']['TXA.4.1'] = objMsgContent.Activity_DTTM;
	tmp['TXA']['TXA.5']['TXA.5.1'] = objMsgContent.ProvId;
	tmp['TXA']['TXA.4']['TXA.6.1'] = objMsgContent.OriginiationDTTM;
	tmp['TXA']['TXA.4']['TXA.7.1'] = objMsgContent.TranscribedDTTM;
	tmp['TXA']['TXA.12']['TXA.12.1'] = objMsgContent.DocumentId;
	tmp['TXA']['TXA.12']['TXA.17.1'] = objMsgContent.DOCStatus.toString().substring(0, 1);

	tmp['OBX']['OBX.1']['OBX.1.1'] = setID;
	tmp['OBX']['OBX.2']['OBX.2.1'] = objMsgContent.ValueType; 
	tmp['OBX']['OBX.5']['OBX.5.1'] = objMsgContent.DocumentBodyRTF;

	if (debug>0) { logger.error(transDescription+' tmp: '+tmp); }	
	// var message = SerializerFactory.getSerializer('HL7V2').fromXML(tmp);

	return tmp;
}

/** getCurrentDateTime

  	- Desc: This function returns current DateTime only YYYYMMDDHHMMSS format

	@return {string} datetime - this string object contains the current DateTime only YYYYMMDDHHMMSS format
*/
function getCurrentDateTime() {
	var today = new Date();
	// set Date with leading zeros if needed
	var tMonth= today.getMonth()+1;
	if(tMonth.toString().length<2) tMonth= '0'+tMonth;
	var tDay= today.getDate()
	if(tDay.toString().length<2) tDay= '0'+tDay;
	var date = today.getFullYear()+''+tMonth+''+tDay;

	// set Time with leading zeros if needed
	var tHour= today.getHours();
	if(tHour.toString().length<2) tHour= '0'+tHour;
	var tMinute= today.getMinutes();
	if(tMinute.toString().length<2) tMinute= '0'+tMinute;
	var tSecond= today.getSeconds();
	if(tSecond.toString().length<2) tSecond= '0'+tSecond;

	var time = tHour+''+tMinute+''+tSecond;
	var dateTime = date+''+time;

	return dateTime;
}

/** occurrences

	-Desc: This function that count occurrences of a substring in a string.

 	@param {String} string               The string
 	@param {String} subString            The sub string to search for
 	@param {Boolean} [allowOverlapping]  Optional. (Default:false)

 	@author Vitim.us https://gist.github.com/victornpb/7736865
 	@see Unit Test https://jsfiddle.net/Victornpb/5axuh96u/
 	@see http://stackoverflow.com/questions/4009756/how-to-count-string-occurrence-in-string/7924240#7924240
 */
function occurrences(string, subString, allowOverlapping) {

    string += "";
    subString += "";
    if (subString.length <= 0) return (string.length + 1);

    var n = 0,
        pos = 0,
        step = allowOverlapping ? 1 : subString.length;

    while (true) {
        pos = string.indexOf(subString, pos);
        if (pos >= 0) {
            ++n;
            pos += step;
        } else break;
    }
    return n;
}

/** parseCSVtoObject

  	- Desc: This function receives a RAW msg string and returns string objectified

   	@param {String} msg - this contains a RAW message (i.e. msg)
	@return {Object} objMsgContents - this object contains objectification of the RAW msg string
*/
function parseCSVtoObject (msg, debug) {

	// var debug=1; // 0= OFF, 1= channelMap, 2= logger.error
	var funcDescription= 'parseCSVtoObject';
	if(debug>0) { logger.error('DEBUG::'+funcDescription+' msg: '+msg); }

	// create obj Message Contents
	var objMsgContents= new Object(); 

// CHS FACILITY NAME
	if(msg['row']['column1'].toString().length>0) {
		objMsgContents.FacName= msg['row']['column1'].toString();
	} else {
		objMsgContents.FacName= '';
	}
	channelMap.put('FacName',objMsgContents.FacName);	
// CHS REGION
	if(msg['row']['column2'].toString().length>0) {
		objMsgContents.ChsRegion= msg['row']['column2'].toString();
	} else {
		objMsgContents.ChsRegion= 'X';
	}
	channelMap.put('ChsRegion',objMsgContents.ChsRegion);
// CHS EHR SYSTEM	
	if(msg['row']['column3'].toString().length>0) {
		objMsgContents.adtEHR= msg['row']['column3'].toString();
	} else {
		objMsgContents.adtEHR= '';
	}
	channelMap.put('adtEHR',objMsgContents.adtEHR);	
// SENDING FACILITY	
	if(msg['row']['column4'].toString().length>0) {
		objMsgContents.SendingFacility= msg['row']['column4'].toString()
	} else {
		objMsgContents.SendingFacility= '';
	}
// RECV FACILITY	
	if(msg['row']['column5'].toString().length>0) {
		objMsgContents.ReceivingFacility= msg['row']['column5'].toString();
	} else {
		objMsgContents.ReceivingFacility= '';
	}
	channelMap.put('ReceivingFacility',objMsgContents.ReceivingFacility);
// PAT MRN	
	if(msg['row']['column6'].toString().length>0) {
		objMsgContents.PatientIdentifier= msg['row']['column6'].toString();
	} else {
		objMsgContents.PatientIdentifier= '';
	}
	channelMap.put('PatientId',objMsgContents.PatientIdentifier);		
// PAT ACCT #	
	if(msg['row']['column7'].toString().length>0) {
		objMsgContents.PatientAcct= msg['row']['column7'].toString();
	} else {
		objMsgContents.PatientAcct= '';
	}
	channelMap.put('PatientAcct',objMsgContents.PatientAcct);	
// PAT LAST NAME	
	if(msg['row']['column8'].toString().length>0) {
		objMsgContents.PatientLastName= msg['row']['column8'].toString();
	} else {
		objMsgContents.PatientLastName= '';
	}
	channelMap.put('PatientLastName',objMsgContents.PatientLastName);
// PAT ADDRESS FIRST NAME	
	if(msg['row']['column9'].toString().length>0) {
		objMsgContents.PatientFirstName= msg['row']['column9'].toString();
	} else {
		objMsgContents.PatientFirstName= '';
	}
	channelMap.put('PatientFirstName',objMsgContents.PatientFirstName);
// PAT ADDRESS DOB	
	if(msg['row']['column10'].toString().length>0) {
		objMsgContents.PatientDOB= msg['row']['column10'].toString();
	} else {
		objMsgContents.PatientDOB= '19000101';
	}
// PAT GENDER	
	if(msg['row']['column11'].toString().length>0) {
		objMsgContents.PatientGender= msg['row']['column11'].toString();
	} else {
		objMsgContents.PatientGender= '';
	}
// PAT PHONE	
	if(msg['row']['column12'].toString().length>0) {
		objMsgContents.PatientPhoneNumber= msg['row']['column12'].toString();
	} else {
		objMsgContents.PatientPhoneNumber= '';
	}
// PAT ADDRESS STREET	
	if(msg['row']['column13'].toString().length>0) {
		objMsgContents.PatientAddressStreet= msg['row']['column13'].toString();
	} else {
		objMsgContents.PatientAddressStreet= '';
	}
	channelMap.put('PatientAddressStreet',objMsgContents.PatientAddressStreet);
// PAT ADDRESS CITY	
	if(msg['row']['column14'].toString().length>0) {
		objMsgContents.PatientAddressCity= msg['row']['column14'].toString();
	} else {
		objMsgContents.PatientAddressCity= '';
	}
	channelMap.put('PatientAddressCity',objMsgContents.PatientAddressCity);
// PAT ADDRESS ST	
	if(msg['row']['column15'].toString().length>0) {
		objMsgContents.PatientAddressState= msg['row']['column15'].toString();
	} else {
		objMsgContents.PatientAddressState= '';
	}
	channelMap.put('PatientAddressState',objMsgContents.PatientAddressState);	
// PAT ADDRESS ZIP
	if(msg['row']['column16'].toString().length>0) {
		objMsgContents.PatientAddressZip= msg['row']['column16'].toString();
	} else {
		objMsgContents.PatientAddressZip= '';
	}
	channelMap.put('PatientAddressZip',objMsgContents.PatientAddressZip);	

	if(debug>0) { logger.error('DEBUG::'+funcDescription+' object: '+JSON.stringify(objMsgContents)); }
	return objMsgContents;
} // end function

/** parseHL7MDMtoObject

  	- Desc: This function receives a RAW HL7v2 MDM message string and returns string objectified
  	- Modified
	2020-03-03 JER - Modified to iterate through the instances of the row XML object into JS Object
	2020-03-27 JER - parse DOB to provide YYYYMMDD format

   	@param {String} msg - this contains a RAW message (i.e. msg)
	@return {Object} objMsgContent - this object contains objectification of the RAW msg string
*/
function parseHL7MDMtoObject (msg, debug) {

	// var debug=1; // 0= OFF, 1= channelMap, 2= logger.error
	var funcDescription= 'parseHL7MDMtoObject';
	var intCount= 0;
	var chkNumeric= false;
	if(debug>0) { logger.error('DEBUG::'+funcDescription+' msg: '+msg); } 

	var arrORUMessageSegments= new Array();

	for each(element in msg.children()) {
		if (element.name()== 'MSH') {
			var msgSegment= new Object();
			msgSegment.SendingApplication= 		element['MSH.3']['MSH.3.1'].toString();
			msgSegment.SendingFacility= 			element['MSH.4']['MSH.4.1'].toString();
			msgSegment.ReceivingApplication=		element['MSH.5']['MSH.5.1'].toString();
			msgSegment.ReceivingFacility=			element['MSH.6']['MSH.6.1'].toString();
			msgSegment.MessageDateTime=			element['MSH.7']['MSH.7.1'].toString();
			msgSegment.MessagesType=				element['MSH.9']['MSH.9.1'].toString();
			msgSegment.MessageEventType=			element['MSH.9']['MSH.9.2'].toString();
			msgSegment.MessageControlId=			element['MSH.10']['MSH.10.1'].toString();
			arrORUMessageSegments.push(msgSegment);
		} else if (element.name()== 'PID') {
			var msgSegment= new Object();
			msgSegment.PatientId= 				element['PID.3']['PID.3.1'].toString();
			msgSegment.LastName=				element['PID.5']['PID.5.1'].toString();
			msgSegment.FirstName=				element['PID.5']['PID.5.2'].toString();
			msgSegment.MiddleName=				element['PID.5']['PID.5.2'].toString();
			msgSegment.DateOfBirth=				element['PID.7']['PID.7.1'].toString();
			msgSegment.Gender=					element['PID.8']['PID.8.1'].toString();
			msgSegment.AccountNumber=			element['PID.18']['PID.18.1'].toString();
			msgSegment.SSN=					element['PID.19']['PID.19.1'].toString();
			arrORUMessageSegments.push(msgSegment);
		} else if (element.name()== 'PV1') {
			msgSegment.VisitNumber=				element['PV1.19']['PV1.19.1'].toString();
			arrORUMessageSegments.push(msgSegment);
		} else if (element.name()== 'TXA') {
			var documentType=					element['TXA.2']['TXA.2.1'].toString();
			msgSegment.DocumentType=				documentType;
			var activityDateTime=				element['TXA.4']['TXA.4.2'].toString().replace(/[^0-9a-zA-Z]/g,'');
			msgSegment.ActivityDateTime=			activityDateTime.substring(0,8);
			msgSegment.OriginationDateTime=		activityDateTime.substring(0,8);
			msgSegment.TranscriptionDateTime=		activityDateTime.substring(0,8);
			msgSegment.ProviderCode=				element['TXA.5']['TXA.5.1'].toString();
			msgSegment.DocumentNumberId=			element['TXA.12']['TXA.12.1'].toString();
			msgSegment.DocumentNamespaceId=		element['TXA.12']['TXA.12.2'].toString();
			arrORUMessageSegments.push(msgSegment);
		} else if (element.name()== 'OBX') {
			msgSegment.ValueType=				documentType;
			msgSegment.ObservationId=			element['OBX.2']['OBX.2.1'].toString();
			arrORUMessageSegments.push(msgSegment);
		}
	}

	if(debug>0) { logger.error('arrORUMessageSegments.length: '+arrORUMessageSegments.length); }
	channelMap.put('arrORUMessageSegments',arrORUMessageSegments);
	return arrORUMessageSegments;
}

/** parseRTFtoObject

  	- Desc: This function receives a RAW msg string and returns string objectified
  	- Modified
	2020-03-03 JER - Modified to iterate through the instances of the row XML object into JS Object
	2020-03-27 JER - parse DOB to provide YYYYMMDD format

   	@param {String} msg - this contains a RAW message (i.e. msg)
	@return {Object} objMsgContent - this object contains objectification of the RAW msg string
*/
function parseRTFtoObject (msg, debug) {

	// var debug=1; // 0= OFF, 1= channelMap, 2= logger.error
	var funcDescription= 'parseRTFtoObject';
	var intCount= 0;
	var chkNumeric= false;
	if(debug>0) { logger.error('DEBUG::'+funcDescription+' msg: '+msg); } 

	// create Message objects
	var arrMsgContents= new Array();

	for each (row in msg['row']) {
		if (row.children().length()>1) {
			if(debug>0) { 
				logger.error('row['+intCount+'].children.length: '+row.children().length()+' row children names: '+row.children());
			}

			for each (child in row.children()) {

				if (child.localName().toString() == 'Patient_ID') {
					chkNumeric= isNumeric(child.toString());
					if(debug>0) {
						logger.error(child.localName().toString()+'.isNumeric:'+chkNumeric);
					}
					if(isNumeric(child.toString())== true) {
						// create each Message object
						var objMsgContent= new Object();

						// Patient ID
						if(child.toString().length>0) {
							objMsgContent.PatId= child.toString();
						} else {
							objMsgContent.PatId= '';
						}
						//channelMap.put('PatId',objMsgContent.PatId);
					}
				}

				if(chkNumeric==  true) {
					// ReportType
					if(child.localName().toString() == 'ReportType') {
						if(child.toString().length>0) {
							objMsgContent.ReportType= child.toString();
						} else {
							objMsgContent.ReportType= 'X';
						}	
						//channelMap.put('ReportType',objMsgContent.ReportType);
					// Activity_DTTM	
					} else if(child.localName().toString() == 'Activity_DTTM') {
						if(child.toString().length>0) {
							objMsgContent.ActivityDT= child.toString();
						} else {
							objMsgContent.ActivityDT= '';
						}	
						//channelMap.put('ActivityDT',objMsgContent.ActivityDT);
					// Prov_ID	
					} else if(child.localName().toString() == 'Prov_ID') {
						if(child.toString().length>0) {
							objMsgContent.ProvId= child.toString();
						} else {
							objMsgContent.ProvId= '';
						}	
						//channelMap.put('ProvId',objMsgContent.ProvId);
					// Prov_Last_Nm
					} else if(child.localName().toString() == 'Prov_Last_Nm') {
						if(child.toString().length>0) {
							objMsgContent.ProvLastNm= child.toString();
						} else {
							objMsgContent.ProvLastNm= '';
						}	
						// channelMap.put('ProvLastNm',objMsgContent.ProvLastNm);
					// Prov_First_Nm
					} else if(child.localName().toString() == 'Prov_First_Nm') {
						if(child.toString().length>0) {
							objMsgContent.ProvFirstNm= child.toString();
						} else {
							objMsgContent.ProvFirstNm= '';
						}	
						// channelMap.put('ProvFirstNm',objMsgContent.ProvFirstNm);
					// Prov_Middle_Nm
					} else if(child.localName().toString() == 'Prov_Middle_Nm') {
						if(child.toString().length>0) {
							objMsgContent.ProvMiddleNm= child.toString();
						} else {
							objMsgContent.ProvMiddleNm= '';
						}	
						// channelMap.put('ProvMiddleNm',objMsgContent.ProvFirstNm);
					// DocumentID
					} else if(child.localName().toString() == 'DocumentID') {
						if(child.toString().length>0) {
							objMsgContent.DocumentId= child.toString();
						} else {
							objMsgContent.DocumentId= '';
						}	
						// channelMap.put('DocumentId',objMsgContent.DocumentId);
					// Value_Type
					} else if(child.localName().toString() == 'Value_Type') {
						if(child.toString().length>0) {
							objMsgContent.ValueType= child.toString();
						} else {
							objMsgContent.ValueType= '';
						}	
						// channelMap.put('ValueType',objMsgContent.ValueType);							
					// Patient_Last_Nm
					} else if(child.localName().toString() == 'Patient_Last_Nm') {
						if(child.toString().length>0) {
							objMsgContent.PatientLastNm= child.toString();
						} else {
							objMsgContent.PatientLastNm= '';
						}	
						// channelMap.put('PatientLastNm',objMsgContent.PatientLastNm);
					// Patient_First_Nm
					} else if(child.localName().toString() == 'Patient_First_Nm') {
						if(child.toString().length>0) {
							objMsgContent.PatientFirstNm= child.toString();
						} else {
							objMsgContent.PatientFirstNm= '';
						}	
						// channelMap.put('PatientFirstNm',objMsgContent.PatientFirstNm);
					// Patient_Middle_Nm
					} else if(child.localName().toString() == 'Patient_Middle_Nm') {
						if(child.toString().length>0) {
							objMsgContent.PatientMiddleNm= child.toString();
						} else {
							objMsgContent.PatientMiddleNm= '';
						}	
						// channelMap.put('PatientMiddleNm',objMsgContent.PatientMiddleNm);
					// Patient_DOB
					} else if(child.localName().toString() == 'Patient_DOB') {
						if(child.toString().length>0) {

							// 2020-03-27 JER - parse DOB to provide YYYYMMDD format
							var strPatientDOB= 	child.toString().replace(/-/g,'').replace(/:/g,'').replace(' ','');
							var newPatientDOB= 	strPatientDOB.substring(0,8);
							if(debug>0) { logger.error('DEBUG::'+funcDescription+' strPatientDOB: '+strPatientDOB+' newPatientDOB: '+newPatientDOB); } 
							objMsgContent.PatientDOB= newPatientDOB;
							// objMsgContent.PatientDOB= child.toString();
						} else {
							objMsgContent.PatientDOB= '';
						}	
						// channelMap.put('PatientDOB',objMsgContent.PatientDOB);
					// Patient_Gender
					} else if(child.localName().toString() == 'Patient_Gender') {
						if(child.toString().length>0) {
							objMsgContent.PatientGender= child.toString();
						} else {
							objMsgContent.PatientGender= '';
						}	
						// channelMap.put('PatientGender',objMsgContent.PatientGender);
					// Encounter_ID
					} else if(child.localName().toString() == 'Encounter_ID') {
						if(child.toString().length>0) {
							objMsgContent.EncounterID= child.toString();
						} else {
							objMsgContent.EncounterID= '';
						}	
						// channelMap.put('EncounterID',objMsgContent.EncounterID);
					// Patient_SSN
					} else if(child.localName().toString() == 'Patient_SSN') {
						if(child.toString().length>0) {
							objMsgContent.PatientSSN= child.toString();
						} else {
							objMsgContent.PatientSSN= '';
						}	
						// channelMap.put('PatientSSN',objMsgContent.PatientSSN);
					// DOCStatus
					} else if(child.localName().toString() == 'DOCStatus') {
						if(child.toString().length>0) {
							objMsgContent.DOCStatus= child.toString();
						} else {
							objMsgContent.DOCStatus= '';
						}	
						// channelMap.put('DOCStatus',objMsgContent.DOCStatus);	
					// OriginiationDTTM
					} else if(child.localName().toString() == 'OriginiationDTTM') {
						if(child.toString().length>0) {
							objMsgContent.OriginiationDTTM= child.toString();
						} else {
							objMsgContent.OriginiationDTTM= '';
						}	
						// channelMap.put('OriginiationDTTM',objMsgContent.OriginiationDTTM);
					// TranscribedDTTM
					} else if(child.localName().toString() == 'TranscribedDTTM') {
						if(child.toString().length>0) {
							objMsgContent.TranscribedDTTM= child.toString();
						} else {
							objMsgContent.TranscribedDTTM= '';
						}	
						// channelMap.put('TranscribedDTTM',objMsgContent.TranscribedDTTM);	
					// Document_Body_RTF
					} else if(child.localName().toString() == 'Document_Body_RTF') {
						if(child.toString().length>0) {
							objMsgContent.DocumentBodyRTF= child.toString();
						} else {
							objMsgContent.DocumentBodyRTF= '';
						}	
						// channelMap.put('DocumentBodyRTF',objMsgContent.DocumentBodyRTF);		
					}

				}
			}

			if(debug>0) { logger.error('DEBUG::'+funcDescription+' object: '+JSON.stringify(objMsgContent)); }
			// store objMsgContent in arrMsgContents
			if(chkNumeric==  true) arrMsgContents.push(objMsgContent);
		}
		intCount++;
		var chkNumeric= false;
	}
	if(debug>0) { logger.error('arrMsgContents.length: '+arrMsgContents.length); }
	channelMap.put('arrMsgContents',arrMsgContents);
	return arrMsgContents;
} // end function

function isNumeric(num){
  return !isNaN(num)
}

/**
	To verify if one string (source) contains another string (target).

	@param {String} arg1 - target string to lookup in the source string
	@return {String} return true if the source string contains the target string
*/
String.prototype.contains = function(arg1) {

	return this.indexOf(arg1) > -1;

};

/**
	To verify if the given string contains HTTP status code.

	@param - no parameter
	@return {String} return true if the string contains the HTTP status code, false otherwise
*/

function isHttpCode2xx(arg1) {
	var code2xx = ['200','201','202','203','204','205','206','207','208'];	
	var length = code2xx.length;

	while(length--) {
   		if (arg1.indexOf(code2xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

function isHttpCode4xx(arg1) {
	var code4xx = ['400','401','402','403','404','405','406','407','408','409','410','411','412','413','414','415','416','417','429','431','451'];	
	var length = code4xx.length;

	while(length--) {
   		if (arg1.indexOf(code4xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

function isHttpCode5xx(arg1) {
	var code5xx = ['500','501','502','503','504','505','506','510','511'];	
	var length = code5xx.length;

	while(length--) {
   		if (arg1.indexOf(code5xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

