	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
//This function is used for checking the word "PDF" in inbound attachment.
function isValidBase64PDF(base64) {
	const ENCODED_PDF_SIGN = "JVBERi"; // %PDF sign
	const PDF_EOF = "JSVFT0YNCg"; // %EOF
	var pdfBase64Size = (base64.length() * (3 / 4)) - 1;

	return (base64.indexOf(ENCODED_PDF_SIGN) != -1 && base64.indexOf(PDF_EOF) != -1 && pdf_base64_size > 36);
}

/**
	To convert JSON date/time format to HL7v2 date only format.

	@param {String} datetime - JSON date/time (yyyy-MM-ddThh:mm:ss.sss-00:00) or (yyyy-MM-dd)
	@return {String} return HL7v2 date (yyyyMMdd)
*/
function convertToHL7v2Date(datetime) { 

	var date = datetime.toString().match(/\d{4}\-\d{2}\-\d{2}/);
	return ((date.length > 0) ? date[0].replace(/\-/g,'') : date.replace(/\-/g,''));

};

/**
	To convert JSON date/time format to HL7v2 date only format.
	Possible formats:
	"2016-01-01T00:00:00.000Z"	JavaScript built-in JSON object
	"2016-01-01T00:00:00-00:00"	ISO 8601

	@param {String} datetime - JSON date/time with offset (yyyy-MM-ddThh:mm:ss.sss-/+00:00) or (yyyy-MM-dd)
	@return {String} return HL7v2 DTM date type
*/
function convertToHL7v2DateTime(datetime) { 

	var date = [];
	var hl7Date = "";
	var timeZome = "";

	if (datetime.indexOf("T") > -1) {

		var zone = datetime.toString().slice(datetime.indexOf("T") + 1);
		if (zone.indexOf("+") > 0) timeZone = "+";
		if (zone.indexOf("-") > 0) timeZone = "-";

		if (datetime.indexOf("Z") > 0) {
			date = datetime.toString().split(/\-|\+|T|:|\./g);
		} else {
			date = datetime.toString().split(/\-|\+|T|:/g);
		}

		for (var i=0; i < 6; i++) {			
			hl7Date += date[i]; 
		}

		var offset = date.slice(6);
		for (var i=0; i < offset.length; i++) {
			timeZone += offset[i];
		}

		return hl7Date + timeZone;

	} else {
		return convertToHL7v2Date(datetime);
	}
};

/**
	Kirby Knight 03/10/2021

	This function will remove all other ROOT.Meta.Destinations nodes that are not passed in the
	destination variable and removes the ROOT.Meta.Destinations[x].Destination node

	@param {Any} destination - 
	@return {String} return description
*/
function formatDestinations(destination) {
var arrDestinations = msg['Meta']['Destinations'];
	//  Start at the end of the array because of the posibility of deleted array items 
	for (var i = arrDestinations.length - 1; i >= 0; i--) {
		if (arrDestinations[i]['Destination'] == destination){
			delete arrDestinations[i]['Destination']
		}else if (arrDestinations[i]['Destination'] != destination){
			arrDestinations.splice(i, 1);
		}
	}
}

/**
	Validates if node is not NULL or "undefined". If the given node is an array, return a string of values.

	@param {String} node - node to extract the data from
	@return {String} return given node value or empty string if such node does not exist
*/
function extract(node) {

	try {
		var result = "";

		if (null == node) return result;

		if ((typeof node) !== "undefined") {

			if (Object.prototype.toString.call(node) === '[object Array]') {
				result = org.apache.commons.lang3.StringUtils.join(node, " ")
			} else 
				result = node;
		}
	} finally {
		return result.toString().trim();
	}
}

/**
	Returns a string containing the feature value (system property) for a specific client database.

	@param {String} featureName - name of the feature, ie: "BetterPTBidirectionalScheduling"
	@param {String} databaseIdentifier - database name or databaseID / TenantID
	@param {String} databaseIdentifierType - what value is provided in databaseIdentifier. current options include "ID" or "NAME"
	@return {String} returns feature value
*/
function getFeatureValueByNameDatabase(featureName, databaseIdentifier, databaseIdentifierType) {
	var paramList = new Packages.java.util.ArrayList();
	paramList.add(featureName);
	paramList.add(databaseIdentifier);
	paramList.add(databaseIdentifierType);

	var sSQL = 'EXEC [dbo].[pr_FeatureValueByNameDatabase] ?, ?, ?';

	try {
		var result = executeSharedDBStatementFhirCh(sSQL, true, paramList);
		var featureValue ;
		if(result.next()){			//Added
		 featureValue = result.getString(1);
		}
		return featureValue;
	} catch (e) {
		logger.error(e);
	}
}

/**
	This function populates the MR and PI patient IDS to channelMap

	@return {String} return description
*/
function jsonPatientIds() {
	for each (patId in msg['Patient']['Identifiers']) {
	if (patId['IDType'].toString() == 'MR'){
		channelMap.put('extPatId', patId['ID'].toString());
	}else if(patId['IDType'].toString() == 'PI'){
		channelMap.put('patId', patId['ID'].toString());
	}
}
}

/**
	Verify if the given node exists.

	@param {String} node - node to verify for existance
	@return {String} return true if the given node exists, false otherwise
*/
function keyExists(node) {
	return ( ((typeof node) !== "undefined") && ((typeof node) !== null) );
}

/**
	Map FHIR codes to corresponding HL7v2.6 codes using the Table number.

	@param {String} FhirCode - FHIR code
	@param {String} tableName - HL7v2.6 Table Name	
	@return {String} return corresponsing HL7v2.6 code if known or "Unknown" result
*/
function convertToHL7v2Table(FhirCode,tableNumber) {

	var code = '';
	if ('undefined' !== typeof FhirCode && null !== FhirCode) {
		code = FhirCode.toString().toLowerCase();	
	}


	switch(tableNumber) {
		case "0001":
			if ('female' == code || 'f' == code) return 'F'; else
			if ('male' == code || 'm' == code) return 'M'; else
			if ('other' == code) return 'O'; else
			return 'U'; 			
		break;

		case "0004":
			// http://hl7.org/fhir/encounter-class
			if ('emergency' == code) return 'E'; else				
			if ('inpatient' == code) return 'I'; else
			if ('outpatient' == code) return 'O'; else
			return "U";
		break;

		case "0190":
			// http://hl7.org/fhir/ValueSet/address-type
			if ('postal' == code) return 'H'; else				
			if ('physical' == code) return 'P'; else
			if ('both' == code) return 'H'; else
			return "M";
		break;		

		case "0201":
			// http://hl7.org/fhir/ValueSet/contact-point-use
			if ('home' == code) return 'PRN'; else				
			if ('work' == code) return 'WPN'; else
			if ('mobile' == code) return 'PRS'; else
			if ('temp' == code) return 'TMP'; else			
			if ('old' == code) return 'OLD'; else
			return "PRN";
		break;	

		case "0202":
			// http://hl7.org/fhir/contact-point-system
			if ('phone' == code) return 'PH'; else				
			if ('fax' == code) return 'FX'; else
			if ('pager' == code) return 'BP'; else
			if ('email' == code) return 'Internet'; else
			return "PH";
		break;	

		case "0063":
			// http://hl7.org/fhir/ValueSet/patient-contact-relationship
			// (SN) Partial list
			if ('parent' == code) return 'PAR'; else
			if ('MTH' == code) return 'MTH'; else
			if ('FTH' == code) return 'FTH'; else	
			if ('caregiver' == code) return 'CGV'; else
			if ('SPS' == code) return 'SPO'; else
			if ('CHILD' == code) return 'CHD'; else
			if ('GRNDCHILD' == code) return 'GCH'; else
			if ('GRPRN' == code) return 'GRP'; else
			if ('SIB' == code) return 'SIB'; else
			if ('BRO' == code) return 'BRO'; else
			if ('SIS' == code) return 'SIS'; else
			if ('friend' == code) return 'FND'; else
			if ('ONESELF' == code) return 'SEL'; else
			if ('emergency' == code) return 'EMC'; else
			return 'UNK';		
		break;
		default:
			return 'U';
	}


}

/**
Executes a SQL statement using a shared database connection stored in the Global Map. If the
connection does not exist, creates it.

@param {String} statement - The SQL statement to execute
@param {Boolean} isQuery - true if the statement is a SELECT, otherwise false
@param {List} paramList - A Java List object containing the parameters for the statement (optional)
 */
function executeSharedDBStatementclientDbCh(statement, isQuery, paramList) {
    var result;

    try {
        var dbConnclientDbCh = globalChannelMap.get("dbConnclientDbCh");

        if (dbConnclientDbCh == null || dbConnclientDbCh.getConnection().isClosed()) {
            dbConnclientDbCh = DatabaseConnectionFactory.createDatabaseConnection($('dbDriverMsSql'), $('dbClient'), $('dbUserFhir'), $('dbPwFhir'));
            globalChannelMap.put("dbConnclientDbCh", dbConnclientDbCh);
        }

        if (paramList == undefined) {
            paramList = new Packages.java.util.ArrayList();
        }

        if (isQuery) {
            result = dbConnclientDbCh.executeCachedQuery(statement, paramList);
        } else {
            result = dbConnclientDbCh.executeUpdate(statement, paramList);
        }
    } catch (e) {
        logger.error(e);
    }
    finally {
        return result;
    }
}

