function useExternalProblemID() {

if (['2804', '2822'].indexOf($('tenantId') + '') > -1) {
    tmp['Visit']['AccountNumber'] = msg['visitexternalproblemid'].toString()
} else {
    tmp['Visit']['AccountNumber'] = msg['visitaccountnumber'].toString();
}
}

