com.mirth.connect.plugins.javascriptrule.script:
/**  Desc: This code works by calculating a checksum for each message to uniquely identify it (we ignore the
	MSH and ORC segments). We save off the most recent 100 unique checksum values in a global 
	array so if the current message has been recently received we are able to ignore it.

	fileName:	     call_filter_msg_evaluate_received_checksum.js
	name:		Call Filter Msg Evaluate Received Checksum 
	authored:		Jeffrey Ritz
	created:		2023-03-06
	desc:		Evaluate Msg Checksum to call aligned with removeParentObject and removeChildObjectFromParentObject function code_templates
			  	true  -> provide filter= true or filter= false
	history:
	2024-02-24 JER - Modified to evaluate removeParentObject and removeChildObjectFromParentObject function calls
*/

var scriptName= 'call_filter_msg_evaluate_received_checksum'
var filter = false;
var received_len_limit = 999;
var debug= 0;

var msg= connectorMessage.getRawData();

if(debug>0) {
	logger.error('DEBUG::'+scriptName+' BEFORE msg getRawData: '+connectorMessage.getRawData());
	logger.error('DEBUG::'+scriptName+' BEFORE msg getEncoded: '+connectorMessage.getEncoded());
	logger.error('DEBUG::'+scriptName+' BEFORE msg getEncodedData: '+connectorMessage.getEncodedData());
	logger.error('DEBUG::'+scriptName+' BEFORE msg toString: '+connectorMessage.toString());
} // end if 

// Remove Object from Json
var checksumValue= getMessageChecksum(msg);
if(debug>0) {
	logger.info('Message BEFORE removeObjectFromJson: '+checksumValue);
	logger.info('Message BEFORE removeObjectFromJson msg: '+msg);
} // end if 
var newMsg= removeObjectFromJson(msg,'Meta');
var checksumValue= getMessageChecksum(newMsg);

if(debug>0) {
	logger.info('Message AFTER removeObjectFromJson: '+checksumValue);
	logger.info('Message AFTER removeObjectFromJson msg: '+JSON.stringify(newMsg));
