	var evalChecksumValue= received.indexOf(checksumValue);
	if (received.indexOf(checksumValue) > -1) {
		filter = true;
	//we haven't seen it recently, so remember it and allow it through
	} else {
		var received_len = received.length;
		if (received_len > received_len_limit) {
			//remove oldest/first entry, shift all entries forward, and add new entry to end
			received.shift();
			received[received_len_limit] = checksumValue;
		} else {
			//add new entry to end of array
			received[received_len] = checksumValue;
		} // end if 
	} // end if 
	if(debug>0) logger.info('INFO::'+scriptName+' EVALUATE received: '+received+' checksumValue: '+checksumValue+' received.indexOf(checksumValue): '+evalChecksumValue+' filter: '+filter);
} // end if 

